import 'dart:typed_data';

const String musicText =
    '';

Uint8List getBinaryFromString(String text) {
  final list = text.split('');
  final List<String> ulist = [];
  for (int i = 0; i < list.length - 1; i += 2) {
    ulist.add('${list[i]}${list[i + 1]}');
  }
  final result = Uint8List.fromList(
    ulist.map((s) => int.parse(s, radix: 16)).toList(),
  );
  print(result.length);
  return result;
}

final Uint8List musicData = getBinaryFromString(musicText);
